function validateForm(e){e.validate({ignore:".ignore",rules:{name:{required:!0,maxlength:150},email:{required:!0,email:!0},avatar:{required:!0,maxlength:510},password:{required:!0,maxlength:30},role_id:{required:!0,number:!0}},messages:{name:{required:"bắt buộc phải nhập title",maxlength:"title có độ dài lớn nhất là {0}"},email:{required:"bắt buộc nhập email",email:"định dạng không phải email"},avatar:{required:"bắt buộc nhập background",maxlength:"đường link vượt {0} kí tự"},password:{required:"bắt buộc nhập password",maxlength:"phần password không vượt quá {0} kí tự"},role_id:{required:"bắt buộc nhập role_id",number:"định dạng là số"}}})}$(document).ready(function(){var e=$(".js-validate-form");e.length&&validateForm(e)});